mapper.update:
UPDATE 	USERS
SET 	SESSION_KEY = #{session_id}
    	,SESSION_LIMIT = #{session_limit}
WHERE 	USER_ID = #{user_id}

mapper.insert:
INSERT INTO USERS (
	USER_NO
    ,USER_ID
    ,USER_PW
    ,USER_NAME
    ,USER_EMAIL
    ,USER_SEX
    ,USER_SIGNATURE
) VALUES (
	USER_NO_SEQ.NEXTVAL
    ,#{user_id}
    ,#{user_pw}
    ,#{user_name}
    ,#{user_email}
    ,#{user_sex}
    ,#{user_signature}
)

mapper.select:
